mapper.select:
SELECT e.*, e.PMT_DD DD, c.NAME CTG, COALESCE(ca.NAME, a.NAME) METHOD
FROM EXP e
LEFT JOIN CTG c ON c.ID = e.CTG_ID
LEFT JOIN CARD ca ON ca.ID = e.CARD_ID
LEFT JOIN ACCT a ON a.ID = e.ACCT_ID
WHERE
    e.USER_ID = #{id}

    AND MONTH(e.PMT_DD) = #{month}
    AND MONTH(e.PMT_DD) = MONTH(CURRENT_DATE)


    AND YEAR(e.PMT_DD) = #{year}
    AND YEAR(e.PMT_DD) = YEAR(CURRENT_DATE)


    AND e.CTG_ID = #{category}

SELECT IFNULL(sum(AMT),0)
FROM EXP
WHERE
    USER_ID = #{id}

    AND MONTH(PMT_DD) = #{month}
    AND MONTH(PMT_DD) = MONTH(CURRENT_DATE)


    AND YEAR(PMT_DD) = #{year}
    AND YEAR(PMT_DD) = YEAR(CURRENT_DATE)

SELECT IFNULL(sum(AMT),0)
FROM EXP
WHERE
    USER_ID = #{id}
    AND TYPE = 'VAR'

    AND MONTH(PMT_DD) = #{month}
    AND MONTH(PMT_DD) = MONTH(CURRENT_DATE)


    AND YEAR(PMT_DD) = #{year}
    AND YEAR(PMT_DD) = YEAR(CURRENT_DATE)

SELECT IFNULL(sum(AMT),0)
FROM EXP
WHERE
    USER_ID = #{id}
    AND TYPE = 'FIX'

    AND MONTH(PMT_DD) = #{month}
    AND MONTH(PMT_DD) = MONTH(CURRENT_DATE)


    AND YEAR(PMT_DD) = #{year}
    AND YEAR(PMT_DD) = YEAR(CURRENT_DATE)

SELECT IFNULL(sum(AMT),0)
FROM EXP
WHERE
    USER_ID = #{id}
    AND CTG_ID = #{category}

    AND MONTH(PMT_DD) = #{month}
    AND MONTH(PMT_DD) = MONTH(CURRENT_DATE)


    AND YEAR(PMT_DD) = #{year}
    AND YEAR(PMT_DD) = YEAR(CURRENT_DATE)

mapper.insert:
INSERT INTO EXP (
    NAME, AMT, CTG_ID, USER_ID, PMT_DD, CARD_ID, ACCT_ID, TYPE
) VALUES (
    #{name}, #{amt}, #{ctgId}, #{userId}, #{dd}, #{cardId}, #{acctId}, #{type}
)

mapper.update:
UPDATE EXP
SET
    NAME = #{name},
    AMT = #{amt},
    CTG_ID = #{ctgId},
    MOD_DT = CURRENT_TIMESTAMP

    , PMT_DD = #{dd}


  , CARD_ID = #{cardId}
  , CARD_ID = null


  , ACCT_ID = #{acctId}
  , ACCT_ID = null

, TYPE = #{type}
WHERE
    ID = #{id} AND USER_ID = #{userId}

mapper.delete:
DELETE FROM EXP
WHERE
    ID = #{id} AND USER_ID = #{userId}
