mapper.select:
SELECT f.*, f.PMT_DD DD, c.NAME CTG
FROM FIXED f
LEFT JOIN CTG c ON c.ID = f.CTG_ID
WHERE
    f.USER_ID = #{id}

    AND f.IS_PAID = #{flag}

mapper.insert:
INSERT INTO FIXED (
    NAME, AMT, CTG_ID, USER_ID, PMT_DD, IS_PAID
    , CARD_ID
    , ACCT_ID
) VALUES (
    #{name}, #{amt}, #{ctgId}, #{userId}

        , NULL
        , #{dd}

    , true
    , false
    , #{cardId}
    , #{acctId}
)

mapper.update:
UPDATE FIXED
SET 
    NAME = #{name},
    AMT = #{amt},
    CTG_ID = #{ctgId},
    MOD_DT = CURRENT_TIMESTAMP

    , PMT_DD = #{dd}

, CARD_ID = #{cardId}
, ACCT_ID = #{acctId}

    , IS_PAID = true
    , IS_PAID = false

WHERE
    ID = #{id} AND USER_ID = #{userId}

mapper.delete:
DELETE FROM FIXED
WHERE
    ID = #{id} AND USER_ID = #{userId}
