mapper.select:
SELECT f.*, f.PMT_DD DD, c.NAME CTG
FROM FIXED f
LEFT JOIN CTG c ON c.ID = f.CTG_ID
WHERE
    f.USER_ID = #{id}

    AND f.IS_PAID = #{flag}

mapper.insert:
INSERT INTO FIXED (
    NAME, AMT, PMT_DD, CTG_ID, USER_ID, IS_PAID
) VALUES (
    #{name}, #{amt}, #{dd}, #{ctgId}, #{userId}
    , #{isPaid}
    , false
)

mapper.delete:
DELETE FROM FIXED
WHERE
    ID = #{id} AND USER_ID = #{userId}
